import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

let pOne;
let pTwo;
let maxO;
let outPut = null;
let flag = 0;

class Hello extends React.Component{
    constructor(props)
    {
      super(props);
      this.state = {
        input:'',
        //0 - start, 1 - maxOdd set, 2 - playerOne set, 3 - playerTwo set 
        gameStage: 0,
      };
      this.addValue = this.addValue.bind(this);
      this.updateInput = this.updateInput.bind(this);
    }

    addValue(evt)
    {
      console.log("Passing: "+ this.state.input)
      evt.preventDefault();
      if(this.state.gameStage === 0)
      {
        maxO = this.state.input;
        //alert('Your input value is: ' + maxO);
        this.setState({gameStage: 1, input:''});

      }
      else if(this.state.gameStage === 1){
        pOne = this.state.input;
        //alert("Player one entered " + pOne);
        this.setState({gameStage: 2, input:''});
      }
      else if (this.state.gameStage === 2) {
        pTwo = this.state.input;
        this.setState({gameStage: 3,input:''});
        //alert("Player two entered " + pTwo);
        flag = 1;
      }
      else{
        alert("what is happening");
      }
      this.setState({input: ''});
    }

    updateInput(evt){
       this.setState({input: evt.target.value});
    }

    calcRes() {
      //if the guesses match
      console.log(pOne + " :p1, p2: " + pTwo);

      let sum = parseInt(pOne) + parseInt(pTwo);
      console.log("pOne + pTwo: " + sum);
      if (pOne === pTwo) {
        return 0;
      }
      //if they add up to the max
      else if (sum === maxO) {
        return 2;
      }
      //if they dont match
      else if (pOne !== pTwo) {
        return 1;

      }
    }

    render()
    {
      let status = null;
      let playerInput="";
      if (!maxO) {
        status = "Please enter a maximum Odd";
      }

      if(this.state.gameStage === 1){
        playerInput = "Player One, a number between 1 and " + maxO;
      }
      else if(this.state.gameStage === 2){
        playerInput = "Player Two, a number between 1 and " + maxO;
      }

      if(this.state.gameStage === 3 && outPut === null){
        outPut = this.calcRes();
        this.props.check();
        //alert("output is: " + outPut);

      }
      return (

      <div>
        <div>
        {status}
        </div>
        <div>
          {playerInput}
        </div>

        <form>
          <input type="text" value={this.state.input} onChange={this.updateInput} /><br/><br/>
          <button onClick={this.addValue} type="submit">Submit</button>
        </form>
      </div>
      );
    }
  }

class Results extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      displayResults: false,
      countdown: 3
    };
    this.timer = 0;
    this.startCounter = this.startCounter.bind(this);
    this.counter = this.counter.bind(this);
  }

  startCounter() {
    if (this.timer === 0 && this.state.countdown > 0) {
      this.timer = setInterval(this.counter, 1000);
    }
  }


  counter(){
    let seconds = this.state.countdown - 1;
    this.setState({
      countdown: seconds
    });
    console.log("countdown counter: " +seconds);
    if(seconds === 0){
      clearInterval(this.timer);
      this.setState({displayResults: true});
    }
    //this.setState({displayResults:true});
  }



  render(){

    let outro = "";

    if (outPut === 0) {
      outro = "Yikes, it's a match!";
    }
    else if (outPut === 1) {
      outro = "Better luck nexttime!";
    }
    else if (outPut === 2) {
      outro = "How the tables have turned...";
    }

    let count = <p>{this.state.countdown}</p>;
    let res =
      <div>
        <div>{outro}</div>
        <div>{pOne}</div>
        <div>{pTwo}</div>
      </div>;

    console.log(outPut+" called it");
//     if(outPut !== null){
//     for(let i = 0; i < 3; i++){
//         // setTimeout(() => {
//  //         console.log(i);
//       this.counter(count, i);
//     //     // }, 1000);
//        };
//     } 
    if(this.state.countdown > 0){
      this.startCounter();
    }

    return(
      <div id="resultsWrap">
        <div> 
          { this.state.displayResults ? res : count }
        </div>
      </div>
    );
  }
}

class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      //0 - getting input, 1 - calculating animation, 2 - ready to display results
      gameState: true
    };
  }

  getResults = () => {
    if(flag === 1){
      console.log(outPut +": b4");
      this.setState({ gameState: false });
      console.log(outPut + ": after");
    }
  }

  render()
  {
    console.log("we out here");

    return(
      <div>
      { this.state.gameState ? <Hello check={this.getResults}/> : <Results /> }
      </div>
    );
  }
}

