import React from 'react';
import ReactDOM from 'react-dom';

function UserInput(props){
 return(
   <div>
    <input type="text" class="userInput"/>
    <button onClick={props.onClick}>Submit Guess</button>
   </div>
 );
}

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      maxOdd: 0,
      playerOne: 0,
      playerTwo: 0,
      whosTurn: 0
    } 
  }

  getOdds(val){
    console.log("in this function");
    if(this.state.maxOdd === 0){
      this.setState({
        maxOdd: val,
      });
    }
  }

  render(){
    let status;

    if(this.state.maxOdd !== 0){
      status = "Max Odd: " + this.state.maxOdd;
    }
    else{
      status = "No max odd selected"
    }
    return(
      <div>
        <div>
          <p> status </p>
        </div>
        <div className="App">
          <UserInput onClick={this.getOdds}/ >
        </div>
      </div>
    );
  }
}

export default App;
