import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let pOne;
let pTwo;
let maxO;

class Hello extends React.Component{
    constructor(props)
    {
      super(props);
      this.state = {
        input:'',
        //0 - start, 1 - maxOdd set, 2 - playerOne set, 3 - playerTwo set 
        gameStage: 0,
      };
      this.addValue = this.addValue.bind(this);
      this.updateInput = this.updateInput.bind(this);
    }

    addValue(evt)
    {
      console.log("Passing: "+ this.state.input)
      evt.preventDefault();
      if(this.state.gameStage === 0)
      {
        maxO = this.state.input;
        alert('Your input value is: ' + maxO);
        this.setState({gameStage: 1, input:''});

      }
      else if(this.state.gameStage == 1){
        pOne = this.state.input;
        alert("Player one entered " + pOne);
        this.setState({gameStage: 2, input:''});
      }
      else if (this.state.gameStage === 2) {
        pTwo = this.state.input;
        this.setState({gameStage: 3,input:''});
        alert("Player two entered " + pTwo);
      }
      else{
        alert("what is happening");
      }
      this.setState({input: ''});
    }

    updateInput(evt){
      console.log(evt.target.value)
      this.setState({input: evt.target.value});

        //this.setState({gameStage: 1});
      }

    render()
    {
      let status;
      if (maxO !== 0) {
        status = "Max Odd is " + maxO;
      }
      else{
        status = "Please enter a maximum Odd";
      }
      return (

      <div>
      <div>status</div>
      <form>
      <input type="text" value={this.state.input} onChange={this.updateInput} /><br/><br/>
      <button onClick={this.addValue} type="submit">Submit</button>
      </form>
      </div>
      )
    }
  }

ReactDOM.render(
  <Hello />,
  document.getElementById('root')
);

