import React from 'react';

function UserInput(props){
 return(
   <div>
    <input type="text" class="userInput"/>
    <button onClick={props.onClick}>Submit Guess</button>
   </div>
 );
}

class Game extends React.Component{
  constructor(props) {
      super(props);
      this.state = {
        squares: 0,
        xIsNext: true,
      }
    }

    getOdds(val){
      console.log("in this function: max odd is "+this.state.maxOdd);
      if(this.state.maxOdd === 0){
        this.setState({
          maxOdd: val,
        });
      }
    }

    render(){
      let status;

      if(this.state.maxOdd !== 0){
        status = "Max Odd: " + this.state.maxOdd;
      }
      else{
        status = "No max odd selected"
      }
      return(
        <div>
          <div>
            <p> status </p>
          </div>
          <div className="App">
            <UserInput onClick={this.getOdds}/ >
          </div>
        </div>
      );
    }
}


class App extends React.Component {
  render() {
        return ( 
            <div className = "game" >
              <Game />
            </div>
        );
    }
}

export default Game;
