import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let pOne;
let pTwo;
let maxO;

class Hello extends React.Component{
    constructor(props)
    {
      super(props);
      this.state = {
        maxOdd: 0,
        playerOne: 0,
        playerTwo: 0,
        //0 - start, 1 - maxOdd set, 2 - playerOne set, 3 - playerTwo set 
        gameStage: 0,
      };
      this.addValue = this.addValue.bind(this);
      this.updateInput = this.updateInput.bind(this);
    }

    addValue(evt)
    {
      console.log("Passing: "+evt.target)
      evt.preventDefault();
      if(this.state.gameStage === 0)
      {

        maxO = this.state.maxOdd;
        alert('Your input value is: ' + maxO);
        this.setState({gameStage: 1, maxOdd:''});

      }
      else if(this.state.gameStage == 1){
        pOne = this.state.playerOne;
        alert("Player one entered " + pOne);
        this.setState({gameStage: 2, playerOne:''});
      }
      else if (this.state.gameStage === 2) {
        pTwo = this.state.playerTwo;
        this.setState({gameStage: 3,playerTwo:''});
        alert("Player two entered " + pTwo);
      }
      else{
        alert("what is happening");
      }
    }

    updateInput(evt){
      console.log(evt.target.value)
      if (this.state.gameStage === 0) {
        this.setState({ maxOdd: evt.target.value });
      }
      else if(this.state.gameStage === 1){
        this.setState({ playerOne: evt.target.value });
      } 
      else if (this.state.gameStage === 2) {
        this.setState({ playerTwo: evt.target.value });
      }

        //this.setState({gameStage: 1});
      }

    render()
    {
      return (
      <form onSubmit={this.addValue}>
      <input type="text" onChange={this.updateInput} /><br/><br/>
      <input type="submit" value="Click Me :)"/>
      </form>
      )
    }
  }

ReactDOM.render(
  <Hello />,
  document.getElementById('root')
);

